SELECT
o.ObjectID,
''					AS [Field name],
''					AS [Original Filename],
''					AS [Asset Media Type ID],
''					AS [Bal ID],
''					AS [Supplier Prefix],

omfd.ConstituentID,		-- ObjectID 8114, 8115, 8116 and 8118 has same artist in two roles


c.CultureSchoolName_Suffix + ' (' + [dbo].[MFAHfx_CON_DateRange] (c.c_ConstituentID) + ')'	AS [Creator Name],  --First Artist?

c.CultureSchoolName_Suffix + 
CASE WHEN c.c_BeginDate = 0 THEN '' ELSE ' (' + [dbo].[MFAHfx_CON_DateRange] (c.c_ConstituentID) + ')' END,


''							AS [Creator ID],
c.cx_Role					AS [Creator Role],

o.TitleDateDisplay + ' (' + LOWER(ISNULL(o.Medium,'')) + ')'	AS [Title],
''							AS [Description],
''							AS [Keywords],
'The Museum of Fine Arts, Houston, Houston, Texas'				AS [Location of Artwork],
''							AS [Location ID],
''							AS [Classification/Box Number],
o.Dated						AS [Year],
CASE WHEN (o.DateBegin/100) + 1 > 1 THEN dbo.MFAHfx_Century(o.DateBegin) ELSE o.Dated END	AS [Century],
d.Dimensions_cm_String		AS Dimensions,
o.Medium					AS [Medium],
'High Res Files Only'		AS [Transparency Status],
'Colour'					AS [Colour],
''							AS [Illustration/Object/Photo],
o.DisplayAccessionNumber	AS [Suppliers Reference],
CAST(CASE WHEN o.ObjectStatusID = 2 THEN
CAST('The Museum of Fine Arts, Houston, ' AS NVARCHAR(MAX)) +
REPLACE(REPLACE(REPLACE(CAST(o.CreditLine AS NVARCHAR(MAX)),'Gift of','gift of'),'Museum purchase','museum purchase'),'Bequest of','bequest of')
ELSE ISNULL(o.CreditLine,'^')END AS NVARCHAR(MAX))				AS [Credit Line 1],
o.ObjRightsType				AS [Credit Line ID 1],
''							AS [Credit Line 2],
'Photographic Copyright'	AS [Credit Line ID 2],
''							AS [Additional notes field]


FROM MFAHv_OBJ									AS o
LEFT OUTER JOIN MFAHv_OBJ_Maker_FirstDisplayed	AS omfd	ON	o.ObjectID = omfd.ObjectID
LEFT OUTER JOIN MFAHv_OBJ_Constituent			AS c	ON	omfd.ObjectID = c.o_ObjectID
												AND			omfd.ConstituentID = c.c_ConstituentID
												AND			omfd.RoleID = c.cx_RoleID
LEFT OUTER JOIN	MFAHv_OBJ_Dimensions_cm			AS d	ON	o.ObjectID = d.ID

